<script>
export default {
  data() {
    return {
      isVisible: false,
      isFixed: false,
      scrollY: 0,
    };
  },
  methods: {
    handleScroll() {
      this.scrollY = window.scrollY;
      if (
        this.scrollY > (300 * window.innerHeight) / 100 &&
        this.scrollY < (450 * window.innerHeight) / 100
      ) {
        this.isVisible = true;
      } else {
        this.isVisible = false;
      }

      if (this.scrollY > (300 * window.innerHeight) / 100) {
        this.isFixed = true;
      } else {
        this.isFixed = false;
      }
    },
  },
  mounted() {
    window.addEventListener("scroll", this.handleScroll);
  },
  beforeDestroy() {
    window.removeEventListener("scroll", this.handleScroll);
  },
};
</script>

<style scoped lang="scss">
@mixin mobile {
  @media (max-width: 767px) {
    @content;
  }
}
.fade-element {
  transition: opacity 0.4s;
  opacity: 0;
  color: var(--skills-textcolor);
  h2 {
    font-size: 5vw;
    @include mobile {
      font-size: 15vw;
    }
  }
}
.fixed {
  position: fixed;
  left: 5%;
  top: 50%;
  transform: translateY(-50%);
}
.absolute {
  position: absolute;
  left: 5%;
  top: calc(150vh + 50%);
  transform: translateY(-50%);
}
.spacer {
  height: 150vh;
}

.Skills {
  display: flex;
  gap: 10%;
  .Skills-list {
    list-style: none;
    font-size: 5vw;
    @include mobile {
      font-size: 15vw;
    }
  }
}
</style>
